import React, { useState } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10000000000000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const Modal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    firstName1: '',
    firstName2: '',
    place: '',
    toy: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = () => {
    console.log('Saved Data:', formData);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <h2>Modify your experience</h2>
        <div>
          <label>
            First Name 1:
            <input
              type="text"
              name="firstName1"
              value={formData.firstName1}
              onChange={handleChange}
            />
          </label>
          <label>
            First Name 2:
            <input
              type="text"
              name="firstName2"
              value={formData.firstName2}
              onChange={handleChange}
            />
          </label>
          <label>
            Favorite Place:
            <input
              type="text"
              name="place"
              value={formData.place}
              onChange={handleChange}
            />
          </label>
          <label>
            Favorite Toy:
            <input
              type="text"
              name="toy"
              value={formData.toy}
              onChange={handleChange}
            />
          </label>
        </div>
        <button onClick={handleSave}>Save</button>
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
