import React, { useRef, useState } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  overflow: hidden;
`;

const CardsContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const CardWrapper = styled.div`
  width: 392px;
  height: 548px;
  perspective: 1000px;
  position: absolute;
`;

const Card = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  transform-style: preserve-3d;
  transform: ${({ isFlipped }) => (isFlipped ? 'rotateY(180deg)' : 'rotateY(0deg)')};
  transition: transform 1s;
`;

const CardFace = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  backface-visibility: hidden;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
`;

const CardFront = styled(CardFace)`
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
`;

const CardBack = styled(CardFace)`
  background-color: white;
  transform: rotateY(180deg);
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  box-shadow: none;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ActionVerite = () => {
  const [clickedCard, setClickedCard] = useState(null);
  const [isFlipped, setIsFlipped] = useState(false);
  const leftCardRef = useRef(null);
  const rightCardRef = useRef(null);

  const handleCardClick = (card) => {
    setClickedCard(card);
    if (card === 'left') {
      gsap.to(leftCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
      gsap.to(rightCardRef.current, { duration: 0.8, x: 1000, opacity: 0 });
    } else {
      gsap.to(rightCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
      gsap.to(leftCardRef.current, { duration: 0.8, x: -1000, opacity: 0 });
    }

    // Retourner la carte apr√®s 2 secondes
    setTimeout(() => {
      setIsFlipped(true);
    }, 2000);
  };

  return (
    <Container>
      <CardsContainer>
        <CardWrapper>
          <Card
            ref={leftCardRef}
            isFlipped={clickedCard === 'left' && isFlipped}
            onClick={() => handleCardClick('left')}
          >
            <CardFront image={require('../images/junebaby.png')} />
            <CardBack>
              <div>Texte affich√© au verso de la carte de gauche</div>
            </CardBack>
          </Card>
        </CardWrapper>

        <CardWrapper>
          <Card
            ref={rightCardRef}
            isFlipped={clickedCard === 'right' && isFlipped}
            onClick={() => handleCardClick('right')}
          >
            <CardFront image={require('../images/junebaby.png')} />
            <CardBack>
              <div>Texte affich√© au verso de la carte de droite</div>
            </CardBack>
          </Card>
        </CardWrapper>
      </CardsContainer>
      <ButtonContainer>
        <Button>
          <span role="img" aria-label="Tour de William">üë§</span> Au tour de William
        </Button>
        <Button>
          <span role="img" aria-label="I love it">‚ù§Ô∏è</span> I love it
        </Button>
      </ButtonContainer>
    </Container>
  );
};

export default ActionVerite;
