import React, { useEffect } from 'react';
import HomeCard from './HomeCard';
import { useCard } from './CardContext';
import Lottie from 'lottie-react';
import animationData from './LottieAnimation'; // Assurez-vous que le chemin est correct

const CardsWrapper = ({ setBackgroundColor }) => {
  const { selectCard, isLottieVisible, finishAnimation } = useCard();

  const handleCardClick = (card) => {
    console.log('Card clicked:', card);
    selectCard(card); // Déclenche l'animation Lottie et la transition de page
  };

  useEffect(() => {
    console.log('Lottie visibility:', isLottieVisible);
    console.log('Animation Data:', animationData); // Vérifiez que les données sont bien chargées
  }, [isLottieVisible]);

  if (isLottieVisible) {
    // Vérifiez que l'animationData est bien chargée
    if (!animationData || !animationData.layers || animationData.layers.length === 0) {
      console.error("L'animation Lottie n'a pas pu être chargée correctement. Vérifiez le fichier JSON.");
      return <div>Erreur de chargement de l'animation</div>;
    }

    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', width: '100vw' }}>
        <Lottie 
          animationData={animationData} 
          loop={false} 
          onComplete={finishAnimation} 
          style={{ width: '50%', height: '50%' }}
        />
      </div>
    );
  }

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      width: '100vw',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '20px',
      padding: '50px'
    }}>
      <HomeCard
        image={require('../images/junebaby.png')}
        rotate="-5deg"
        onMouseEnter={() => {
          if (setBackgroundColor) {
            setBackgroundColor('#FFD700');
          } else {
            console.error('setBackgroundColor is not a function');
          }
        }}
        onMouseLeave={() => {
          if (setBackgroundColor) {
            setBackgroundColor('#FBF8F1');
          }
        }}
        onClick={() => {
          handleCardClick('card1');
          if (setBackgroundColor) {
            setBackgroundColor('#FBF8F1');
          }
        }}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(-5deg)',
          backgroundColor: '#FBF8F1',
        }}
      />
      <HomeCard
        image={require('../images/yourself.png')}
        rotate="0deg"
        onMouseEnter={() => {
          if (setBackgroundColor) {
            setBackgroundColor('#FF69B4');
          }
        }}
        onMouseLeave={() => {
          if (setBackgroundColor) {
            setBackgroundColor('#FBF8F1');
          }
        }}
        onClick={() => handleCardClick('card2')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(0deg)',
          backgroundColor: '#FBF8F1',
        }}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="5deg"
        onMouseEnter={() => {
          if (setBackgroundColor) {
            setBackgroundColor('#FF4500');
          }
        }}
        onMouseLeave={() => {
          if (setBackgroundColor) {
            setBackgroundColor('#FBF8F1');
          }
        }}
        onClick={() => handleCardClick('card3')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(5deg)',
          backgroundColor: '#FF4500',
        }}
      />
    </div>
  );
};

export default CardsWrapper;
