import React, { useEffect } from 'react';
import HomeCard from './HomeCard';
import { useCard } from './CardContext';

const CardsWrapper = ({ setBackgroundColor }) => {
  const { selectedCard, resetCard } = useCard();

  useEffect(() => {
    console.log('Current selectedCard:', selectedCard);
  }, [selectedCard]);

  const handleCardClick = (card) => {
    console.log('Card clicked:', card);
    if (!selectedCard) {
      resetCard(card);  // Remettre la carte à son état initial si elle a été sélectionnée
    } else {
      console.log('A card is already selected:', selectedCard);
    }
  };

  if (selectedCard) {
    console.log('Rendering selected card only:', selectedCard);
    // Logique d'affichage si une carte est sélectionnée
    return (
      <div style={{
        display: 'flex',
        flexDirection: 'row',
        width: '100vw',
        height: '100vh',
        justifyContent: 'center',
        alignItems: 'center',
        gap: '20px', // Espace entre les cartes
        padding: '50px' // Pour centrer les cartes
      }}>
        {/* Vous pouvez afficher ici la carte sélectionnée uniquement */}
      </div>
    );
  }

  console.log('Rendering all cards.');
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      width: '100vw',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '20px', // Espace entre les cartes
      padding: '50px' // Pour centrer les cartes
    }}>
      <HomeCard
        image={require('../images/junebaby.png')}
        rotate="-5deg"
        onMouseEnter={() => setBackgroundColor('#FFD700')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={() => handleCardClick('card1')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(-5deg)', // Appliquer la rotation
          backgroundColor: '#FBF8F1', // Fond par défaut
        }}
      />
      <HomeCard
        image={require('../images/yourself.png')}
        rotate="0deg"
        onMouseEnter={() => setBackgroundColor('#FF69B4')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={() => handleCardClick('card2')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(0deg)', // Appliquer la rotation
          backgroundColor: '#FBF8F1', // Fond par défaut
        }}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="5deg"
        onMouseEnter={() => setBackgroundColor('#FF4500')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={() => handleCardClick('card3')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(5deg)', // Appliquer la rotation
          backgroundColor: '#FF4500', // Fond par défaut
        }}
      />
    </div>
  );
};

export default CardsWrapper;
