import React, { useRef } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const LineWrapper = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999; /* Ensure it's above all other elements */
`;

const LineSVG = styled.svg`
  width: 100%;
  height: 100%;
`;

const FullScreenLine = () => {
  const lineRef = useRef(null);

  const startAnimation = () => {
    gsap.to(lineRef.current, {
      strokeDashoffset: 0,
      duration: 3, // Adjust duration as needed
      ease: "power2.inOut",
      onComplete: () => {
        // Here you can add code to transition to the next page or hide the line
      }
    });
  };

  return (
    <LineWrapper>
      <LineSVG
        ref={lineRef}
        viewBox="0 0 2291 1821"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          d="M495 1102L1125.81 1328.97C1267.93 1380.11 1425.68 1362.92 1553.45 1282.37L1633.82 1231.7C1863.66 1086.8 1895.52 764.218 1698.48 577.15V577.15C1556.36 442.229 1339.43 424.591 1177.38 534.782L1045.86 624.217C865.688 746.732 634.084 764.587 437.266 671.133L199 558"
          stroke="#77FFEE"
          strokeWidth="925"
          strokeDasharray="2291"
          strokeDashoffset="2291"
        />
      </LineSVG>
    </LineWrapper>
  );
};

export default FullScreenLine;
