import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const RoleplayWrapper = styled.div`
  background-color: #FFD700;
  padding: 20px;
  border-radius: 12px;
  max-width: 600px;
  margin: auto;
  text-align: center;
`;

const Title = styled.h1`
  font-family: 'Poppins', sans-serif;
  font-size: 24px;
  font-weight: bold;
  color: #000;
`;

const Description = styled.p`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  color: #333;
  margin-top: 20px;
`;

const Roleplay = () => {
  const [roleData, setRoleData] = useState(null);

  useEffect(() => {
    const fetchRoleData = async () => {
      try {
        const response = await axios.get('http://localhost:1812/api/roles'); // Remplacez par l'URL correcte de votre API
        setRoleData(response.data[0]); // Charge le premier rôle (à ajuster selon vos besoins)
      } catch (error) {
        console.error('Erreur lors du chargement des rôles:', error);
      }
    };

    fetchRoleData();
  }, []);

  if (!roleData) return <p>Chargement...</p>;

  return (
    <RoleplayWrapper>
      <Title>{roleData.title}</Title>
      <Description>{roleData.description}</Description>
    </RoleplayWrapper>
  );
};

export default Roleplay;
