import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const OverlayWrapper = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #FF4500; /* La couleur de l'overlay */
  clip-path: circle(0% at 50% 50%); /* Début du masque en cercle */
  z-index: 2; /* Assurez-vous que l'overlay est au-dessus de tout */
`;

const Overlay = ({ triggerAnimation }) => {
  const overlayRef = useRef();

  useEffect(() => {
    if (triggerAnimation) {
      gsap.to(overlayRef.current, { 
        duration: 1.5, 
        clipPath: 'circle(150% at 50% 50%)', // Expansion du masque en cercle
        ease: 'power2.out' 
      });
    }
  }, [triggerAnimation]);

  return <OverlayWrapper ref={overlayRef} />;
};

export default Overlay;
