import React, { useState, useEffect } from 'react';
import LottieAnimation from './LottieAnimation';
import ActionVerite from './ActionVerite';  // Assurez-vous que ces composants sont bien importés
import Roleplay from './Roleplay';
import { useCard } from './CardContext';

const CardTransitionPage = ({ onComplete, onBack }) => {
  const { selectedCard, isLottieVisible, finishAnimation } = useCard();
  const [showNewContent, setShowNewContent] = useState(false);

  useEffect(() => {
    if (!isLottieVisible && selectedCard) {
      const timer = setTimeout(() => {
        setShowNewContent(true);
      }, 1000); // Délai de 1 seconde après la fin de l'animation

      return () => clearTimeout(timer);
    }
  }, [isLottieVisible, selectedCard]);

  const getLottieData = () => {
    switch (selectedCard) {
      case 'card1':
        return require('../test.json'); // Chemin correct vers le fichier Lottie
      case 'card2':
        return require('../path/to/lottiefile2.json'); // Chemin correct vers le fichier Lottie
      case 'card3':
        return require('../path/to/lottiefile3.json'); // Chemin correct vers le fichier Lottie
      default:
        return null;
    }
  };

  const getNewContent = () => {
    if (!showNewContent) return null;

    switch (selectedCard) {
      case 'card1':
        return <ActionVerite />;  // Affiche le composant ActionVerite après l'animation
      case 'card2':
        return <div>New content for Card 2</div>;  // Remplacez ce placeholder par le composant approprié
      case 'card3':
        return <Roleplay />;  // Affiche le composant Roleplay après l'animation
      default:
        return null;
    }
  };

  return (
    <div>
      {isLottieVisible && getLottieData() && (
        <LottieAnimation
          animationData={getLottieData()}
          onComplete={finishAnimation}
        />
      )}
      {!isLottieVisible && showNewContent && (
        <div>
          {getNewContent()}
          <button onClick={onBack}>Back</button>
        </div>
      )}
    </div>
  );
};

export default CardTransitionPage;
