import React, { createContext, useContext, useState } from 'react';

const CardContext = createContext();

export const CardProvider = ({ children }) => {
  const [selectedCard, setSelectedCard] = useState(null);
  const [isLottieVisible, setIsLottieVisible] = useState(false);

  const selectCard = (card) => {
    console.log('selectCard called with:', card);
    setSelectedCard(card);
    setIsLottieVisible(true);
    console.log('isLottieVisible set to true');
  };

  const finishAnimation = () => {
    console.log('finishAnimation called');
    setIsLottieVisible(false);
    setSelectedCard(null);
    console.log('isLottieVisible set to false, selectedCard reset to null');
  };

  return (
    <CardContext.Provider value={{ selectCard, isLottieVisible, finishAnimation }}>
      {children}
    </CardContext.Provider>
  );
};

export const useCard = () => {
  const context = useContext(CardContext);
  console.log('useCard context:', context);
  return context;
};
