import React, { useRef } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';
import Logo from './Logo';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
  z-index: 10;
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const LogoContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  position: absolute;
  top: 0;
  left: 0;
`;

const EntranceAnimation = ({ onEnterComplete, setAnimateLogo }) => {
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const logoRef = useRef(null); // Référence pour le logo

  const handleEnter = () => {
    setAnimateLogo(true);

    // Animation pour réduire le logo à 200px et le positionner à 50px du haut, centré horizontalement
    gsap.to(logoRef.current, {
      duration: 1.5,
      width: 200, // Réduire la largeur à 200px
      height: 200, // Réduire la hauteur à 200px
      top: 50, // Positionner à 50px du haut
      left: '50%', // Centrer horizontalement
      x: '-50%', // Ajuster pour centrer correctement
      ease: 'power2.inOut',
      position: 'absolute',
    });

    // Animation pour les overlays et le bouton
    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }

        onEnterComplete(); // Appel pour indiquer que l'animation est terminée
      },
    });
  };

  return (
    <BackgroundContainer>
      <LogoContainer ref={logoRef}>
        <Logo />
      </LogoContainer>
      <WhiteOverlay ref={overlayRef} />
      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

export default EntranceAnimation;
