import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100%;
  width: 100%;
  padding: 20px;
`;

const Card = styled.div`
  width: 392px;
  height: 548px;
  background-color: #FBF8F1;
  border-radius: 16px;
  padding: 48px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  cursor: pointer;
  text-align: center;
`;

const Title = styled.h1`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 700;
  color: #FFD700;
  margin-bottom: 20px;
`;

const Content = styled.p`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 400;
  color: #000000;
  margin-bottom: 30px;
`;

const Footer = styled.div`
  font-family: 'Poppins', sans-serif;
  font-size: 12px;
  font-weight: 700;
  color: #000000;
  margin-top: 20px;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: rotate(7deg) scale(1.05);
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const Roleplay = () => {
  const [role, setRole] = useState(null);

  useEffect(() => {
    const fetchRole = async () => {
      try {
        const response = await axios.get('http://localhost:1812/api/roles'); // Remplacez par l'URL correcte de votre API
        setRole(response.data[0]); // Chargez le premier r√¥le ou ajustez selon votre logique
      } catch (error) {
        console.error('Erreur lors du chargement des donn√©es de roleplay:', error);
      }
    };

    fetchRole();
  }, []);

  if (!role) return <p>Chargement...</p>;

  return (
    <PageContainer>
      <Card>
        <Title>{role.title}</Title>
        <Content>{role.description}</Content>
        <Footer>LoveTogether</Footer>
      </Card>

      <ButtonContainer>
        <Button>
          <span role="img" aria-label="Another one">üîÑ</span> Another one
        </Button>
        <Button>
          <span role="img" aria-label="I want to try it">‚ù§Ô∏è</span> I want to try it
        </Button>
      </ButtonContainer>
    </PageContainer>
  );
};

export default Roleplay;
