import React from 'react';
import styled from 'styled-components';

const GrainWrapper = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1000; /* Assurez-vous que le z-index est assez élevé pour être au-dessus du reste */
  background: url('path-to-your-grain-texture.png'); /* Remplacez par le chemin vers votre texture */
  mix-blend-mode: multiply;
  opacity: 0.3; /* Ajustez l'opacité pour l'intensité souhaitée */
`;

const GrainEffect = () => {
  return <GrainWrapper />;
};

export default GrainEffect;
