import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 20px;
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 16px;
`;

const ToyGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const ToyButton = styled.button`
  background: ${props => props.isSelected ? '#ff4500' : '#fff'};
  border: 1px solid ${props => props.isSelected ? '#ff4500' : '#ddd'};
  color: ${props => props.isSelected ? '#fff' : '#000'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
    border-color: #ff4500;
  }
`;

const ColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const SmallColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const InputField = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Modal = ({ isOpen, onClose, onSave, selectedToys, formData, setFormData }) => {
  const [toys, setToys] = useState([]);
  const [selectedToysState, setSelectedToysState] = useState(selectedToys || []);

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await fetch('http://localhost:1812/api/toys');
        const data = await response.json();
        setToys(data);
      } catch (error) {
        console.error('Error fetching toys:', error);
      }
    };

    fetchToys();

    const savedPreferences = localStorage.getItem('userPreferences');
    if (savedPreferences) {
      const savedData = JSON.parse(savedPreferences);
      setFormData({
        nameMan: savedData.nameMan || '',
        nameWoman: savedData.nameWoman || '',
      });
      setSelectedToysState(savedData.selectedToys || []);
    }
  }, [setFormData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const toggleToySelection = (toy) => {
    setSelectedToysState(prevSelectedToys =>
      prevSelectedToys.includes(toy)
        ? prevSelectedToys.filter(t => t !== toy)
        : [...prevSelectedToys, toy]
    );
  };

  const handleSave = () => {
    const dataToSave = { ...formData, selectedToys: selectedToysState };

    // Sauvegarder les données dans le localStorage
    localStorage.setItem('userPreferences', JSON.stringify(dataToSave));

    // Appeler la fonction onSave avec les données sauvegardées
    onSave(dataToSave);
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Modify your experience</SectionTitle>
        <ColumnContainer>
          <InputField
            type="text"
            name="nameMan"
            placeholder="Name Man"
            value={formData.nameMan || ''}
            onChange={handleChange}
          />
          <InputField
            type="text"
            name="nameWoman"
            placeholder="Name Woman"
            value={formData.nameWoman || ''}
            onChange={handleChange}
          />
          <SmallColumnContainer>
            <SubTitle>Select your toys</SubTitle>
            <ToyGrid>
              {toys.map((toy, index) => (
                <ToyButton
                  key={index}
                  isSelected={selectedToysState.includes(toy.name)}
                  onClick={() => toggleToySelection(toy.name)}
                >
                  {toy.name}
                </ToyButton>
              ))}
            </ToyGrid>
          </SmallColumnContainer>
        </ColumnContainer>
        <button onClick={handleSave}>Save</button>
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
