import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const CardContainer = styled.div`
  width: 392px;
  height: 548px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  transform: translate(${props => props.translateX}px, ${props => props.translateY}px) rotate(${props => props.rotate || '0deg'});
  transition: transform 0.6s cubic-bezier(0.22, 1, 0.36, 1), box-shadow 0.6s ease;
  cursor: pointer;

  &:hover {
    box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.3);
    transform: translateY(-10px) rotate(${props => props.rotate || '0deg'});
  }
`;

const HomeCard = ({ image, rotate, onMouseEnter, onMouseLeave }) => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    const rect = e.target.getBoundingClientRect();
    const offsetX = (e.clientX - rect.left - rect.width / 10) / 20; // Ajustez la sensibilité ici
    const offsetY = (e.clientY - rect.top - rect.height / 10) / 20;
    setPosition({ x: offsetX, y: offsetY });
    console.log('Mouse moved:', { offsetX, offsetY });
  };

  const handleMouseLeave = () => {
    setPosition({ x: 0, y: 0 });
    console.log('Mouse left card.');
    if (onMouseLeave) {
      onMouseLeave(); // Appelle la fonction onMouseLeave pour changer la couleur de fond
    }
  };

  const handleClick = () => {
    console.log('Card clicked:', image);
    gsap.to(cardRef.current, { duration: 1, x: 2000, ease: "power2.out" });
  };

  return (
    <CardContainer
      ref={cardRef} // Utilisation de la référence
      image={image}
      rotate={rotate}
      translateX={position.x}
      translateY={position.y}
      onMouseMove={handleMouseMove}
      onMouseEnter={onMouseEnter} // Appelle la fonction onMouseEnter pour changer la couleur de fond
      onMouseLeave={handleMouseLeave}
      onClick={handleClick} // Gère l'événement de clic pour déclencher l'animation GSAP
    >
      {/* Vous pouvez ajouter du contenu ici */}
    </CardContainer>
  );
};

export default HomeCard;
