import React, { useState } from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  width: 339px;
  height: 474px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  transform: translate(${props => props.translateX}px, ${props => props.translateY}px) rotate(${props => props.rotate || '0deg'});
  transition: transform 0.6s cubic-bezier(0.22, 1, 0.36, 1), box-shadow 0.6s ease;
`;

const HomeCard = ({ image, rotate, onMouseEnter, onMouseLeave }) => {
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    const rect = e.target.getBoundingClientRect();
    const offsetX = (e.clientX - rect.left - rect.width / 10) / 50; // Ajustez la sensibilité ici
    const offsetY = (e.clientY - rect.top - rect.height / 10) / 50;
    setPosition({ x: offsetX, y: offsetY });
  };

  const handleMouseLeave = () => {
    setPosition({ x: 0, y: 0 });
    onMouseLeave(); // Appelle la fonction onMouseLeave pour changer la couleur de fond
  };

  return (
    <CardContainer
      image={image}
      rotate={rotate}
      translateX={position.x}
      translateY={position.y}
      onMouseMove={handleMouseMove}
      onMouseEnter={onMouseEnter} // Appelle la fonction onMouseEnter pour changer la couleur de fond
      onMouseLeave={handleMouseLeave}
    >
      {/* Vous pouvez ajouter du contenu ici */}
    </CardContainer>
  );
};

export default HomeCard;
