import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardTransitionPage from './components/CardTransitionPage';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import LoadingScreen from './components/LoadingScreen';

const MainContent = () => {
  const { selectedCard, isLottieVisible, finishAnimation, resetCard } = useCard();

  return (
    <div>
      <Navbar />  {/* Navbar avec NavbarLeft et NavbarRight */}

      {/* Affiche le CardsWrapper tant qu'aucune carte n'est sélectionnée */}
      {!selectedCard && (
        <CardsWrapper setBackgroundColor={(color) => document.body.style.backgroundColor = color} />
      )}

      {/* Affiche CardTransitionPage après la sélection d'une carte */}
      {selectedCard && (
        <CardTransitionPage
          selectedCard={selectedCard}
          onComplete={finishAnimation}
          onBack={resetCard}
        />
      )}
    </div>
  );
};

const MainComponent = () => {
  const [loading, setLoading] = useState(true);

  const handleEnter = () => {
    setLoading(false);
  };

  if (loading) {
    return <LoadingScreen onEnter={handleEnter} />;
  }

  return (
    <CardProvider>
      <PageProvider>
        <MainContent />
      </PageProvider>
      <GrainEffect />
    </CardProvider>
  );
};

export default MainComponent;
