import React, { useState } from 'react';
import NavbarLeft from './NavbarLeft';
import TransitionPage from './TransitionPage';
import CardsWrapper from './CardsWrapper';
import { CardProvider, useCard } from './CardContext';

const MainContent = () => {
  const { selectedCard, isLottieVisible, finishAnimation, resetCard } = useCard();

  return (
    <div>
      <NavbarLeft />

      {!selectedCard && (
        <CardsWrapper setBackgroundColor={(color) => document.body.style.backgroundColor = color} />
      )}

      {selectedCard && (
        <TransitionPage
          selectedCard={selectedCard}
          onComplete={finishAnimation}
          onBack={resetCard}
        />
      )}
    </div>
  );
};

const MainComponent = () => (
  <CardProvider>
    <MainContent />
  </CardProvider>
);

export default MainComponent;
