import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import ActionVerite from './ActionVerite'; // Assurez-vous d'importer votre composant principal

const CardsBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #faf3d9;
  z-index: 100000000;
`;

const Card = styled.div`
width: 339px;
height: 474px;
  background-color: ${({ color }) => color};
  position: absolute;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.1);

  /* Positions for each card */
  ${({ index }) =>
    index === 0 && `top: -40px; left: -80px; transform: rotate(10deg);`}
  ${({ index }) =>
    index === 1 && `top: -60px; left: 100px; transform: rotate(-15deg);`}
  ${({ index }) =>
    index === 2 && `top: -50px; right: 100px; transform: rotate(20deg);`}
  ${({ index }) =>
    index === 3 && `top: -30px; right: -90px; transform: rotate(-5deg);`}
  ${({ index }) =>
    index === 4 && `top: 150px; right: -100px; transform: rotate(15deg);`}
  ${({ index }) =>
    index === 5 && `bottom: 150px; right: -120px; transform: rotate(-10deg);`}
  ${({ index }) =>
    index === 6 && `bottom: 40px; right: -80px; transform: rotate(5deg);`}
  ${({ index }) =>
    index === 7 && `bottom: -30px; right: 100px; transform: rotate(-20deg);`}
  ${({ index }) =>
    index === 8 && `bottom: -60px; left: 100px; transform: rotate(15deg);`}
  ${({ index }) =>
    index === 9 && `bottom: -50px; left: -80px; transform: rotate(-10deg);`}
  ${({ index }) =>
    index === 10 && `bottom: 40px; left: -100px; transform: rotate(20deg);`}
  ${({ index }) =>
    index === 11 && `bottom: 150px; left: -120px; transform: rotate(-5deg);`}
  ${({ index }) =>
    index === 12 && `top: 150px; left: -100px; transform: rotate(10deg);`}
  ${({ index }) =>
    index === 13 && `top: 60px; left: 60px; transform: rotate(-15deg);`}
  ${({ index }) =>
    index === 14 && `top: 60px; right: 60px; transform: rotate(15deg);`}
  ${({ index }) =>
    index === 15 && `top: 150px; right: 100px; transform: rotate(-20deg);`}
  ${({ index }) =>
    index === 16 && `top: 60px; left: -120px; transform: rotate(10deg);`}
  ${({ index }) =>
    index === 17 && `bottom: 60px; right: 120px; transform: rotate(-10deg);`}
`;

const Content = styled.div`
  position: relative;
  z-index: 10;
  text-align: center;
  max-width: 800px;
`;

const Title = styled.h1`
  font-size: 4rem;
  margin: 0;
  color: #191919;
  opacity: 0;
  animation: fadeIn 2s forwards;
`;

const Subtitle = styled.h2`
  font-size: 3rem;
  margin-top: 0.5rem;
  color: #191919;
  opacity: 0;
  animation: fadeIn 3s forwards;
`;

const fadeInKeyframes = `
  @keyframes fadeIn {
    to {
      opacity: 1;
    }
  }
`;

const colors = [
  '#d7f07d', '#fcba83', '#5ed5fb', '#f979b4', '#fc83d7', '#abffb0',
  '#f8c6f2', '#83fcba', '#fcd783', '#83a9fc', '#f783fc', '#f8fc83',
  '#a9fcb0', '#fcf3a9', '#b0a9fc', '#83fca9', '#fc83a9', '#fca983'
];

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Masquer l'écran de chargement après 2 secondes
    const timer = setTimeout(() => {
      setLoading(false);
    }, 2000);

    return () => clearTimeout(timer); // Nettoyage du timer
  }, []);

  if (loading) {
    return (
      <>
        <style>{fadeInKeyframes}</style>
        <CardsBackground>
          {Array.from({ length: 18 }).map((_, index) => (
            <Card key={index} index={index} color={colors[index % colors.length]} />
          ))}
          <Content>
            <Title>A NIGHT OF CONVERSATION</Title>
            <Subtitle>EXPLORING THE CONFLICTS IN DESIGN</Subtitle>
          </Content>
        </CardsBackground>
      </>
    );
  }

  // Une fois le chargement terminé, afficher le composant principal
}

export default App;
