import React from 'react';
import LottieAnimation from './LottieAnimation';
import ActionVerite from './ActionVerite';
import Roleplay from './Roleplay';
import { useCard } from './CardContext';

const TransitionPage = ({ onComplete, onBack }) => {
  const { selectedCard } = useCard();

  const getLottieData = () => {
    switch (selectedCard) {
      case 'card1':
        return require('../test.json'); // Remplacez par le chemin correct
      case 'card2':
        return require('../test.json'); // Remplacez par le chemin correct
      case 'card3':
        return require('../test.json'); // Remplacez par le chemin correct
      default:
        return null;
    }
  };

  const getNewContent = () => {
    switch (selectedCard) {
      case 'card1':
        return <ActionVerite />;
      case 'card2':
        return <div>New content for Card 2</div>;
      case 'card3':
        return <Roleplay />;
      default:
        return null;
    }
  };

  return (
    <>
      <LottieAnimation
        animationData={getLottieData()}
        onComplete={onComplete} // Appelée lorsque l'animation est terminée
      />

      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2em', color: '#333', flexDirection: 'column' }}>
        {getNewContent()}
        <button onClick={onBack}>Back</button> {/* Bouton pour revenir en arrière */}
      </div>
    </>
  );
};

export default TransitionPage;
