import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import lowIntensityImage from '../images/logo-5.svg';
import mediumIntensityImage from '../images/whitelogo-10.svg';
import highIntensityImage from '../images/whitelogo-20.svg';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const getBackgroundStyle = (intensities) => {
  const intensityColors = {
    'Warm-Up': '#FBF8F1',
    'Foreplay': '#D51C2C',
    'MainEvent': '#5A0C13'
  };

  const intensityImages = {
    'Warm-Up': lowIntensityImage,
    'Foreplay': mediumIntensityImage,
    'MainEvent': highIntensityImage
  };

  if (intensities.length === 0) return '#FBF8F1';

  if (intensities.length === 1) {
    return `
      background-color: ${intensityColors[intensities[0]]};
      background-image: url(${intensityImages[intensities[0]]});
    `;
  }

  const gradientStops = intensities.map((intensity, index) => {
    const percentage = (index / (intensities.length - 1)) * 100;
    return `${intensityColors[intensity]} ${percentage}%`;
  });

  const imageLayersStyle = intensities.map((intensity, index) => {
    const percentage = (index / intensities.length) * 100;
    return `url(${intensityImages[intensity]}) ${percentage}% ${percentage + (100 / intensities.length)}%`;
  }).join(', ');

  return `
    background: linear-gradient(to right, ${gradientStops.join(', ')});
    background-image: ${imageLayersStyle};
  `;
};

const ModalContent = styled.div`
  display: flex;
  width: 90%;
  height: 90vh;
  max-width: 1200px;
  ${({ intensities }) => getBackgroundStyle(intensities)}
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 20px;
  overflow: hidden;
  transition: background 0.3s ease;
`;

const FormSection = styled.div`
  flex: 1;
  padding: 48px;
  overflow-y: auto;
  background-color: rgba(255, 255, 255, 0.9);
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const CardSection = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Group = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const Title = styled.h2`
  font-size: 32px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
`;

const Form = styled.form`
  display: flex;
