import React, { useState } from 'react';
import { PageProvider } from './components/PageContext'; // Importez le contexte
import styled from 'styled-components';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import GrainEffect from './components/GrainEffect';
import LoadingScreen from './components/LoadingScreen';

const BackgroundWrapper = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: ${props => props.backgroundColor || '#white'};
  z-index: -2;
`;

const AppWrapper = styled.div`
  font-family: 'Poppins', sans-serif;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  position: relative;
  z-index: 1;
  transition: background-color 0.3s ease;
`;

function App() {
  const [backgroundColor, setBackgroundColor] = useState('#FBF8F1');

  return (
    <PageProvider>
      <AppWrapper>
        <LoadingScreen /> 
        <Navbar />
        <GrainEffect />  {/* Effet de grain */}
        <BackgroundWrapper backgroundColor={backgroundColor} />
        <CardsWrapper setBackgroundColor={setBackgroundColor} />
      </AppWrapper>
    </PageProvider>
  );
}

export default App;
