import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardTransitionPage from './components/CardTransitionPage';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import LoadingScreen from './components/LoadingScreen';
import Logo from './components/Logo'; // Import du composant Logo

const MainComponent = () => {
  const [loading, setLoading] = useState(true);
  const [logoPosition, setLogoPosition] = useState({
    size: '100%',
    top: '50%',
    left: '50%',
  });

  const handleEnter = () => {
    // Définir la nouvelle position et taille du logo après la disparition de LoadingScreen
    setLogoPosition({
      size: '100px',
      top: '20px',
      left: '20px',
    });

    // Faire disparaître le LoadingScreen après le clic
    setTimeout(() => {
      setLoading(false);
    }, 1000); // Donne le temps à l'animation du logo de se terminer
  };

  return (
    <CardProvider>
      <PageProvider>
        <div>
          {/* Le logo est au-dessus du LoadingScreen */}
          <Logo size={logoPosition.size} top={logoPosition.top} left={logoPosition.left} />
          {loading ? (
            <LoadingScreen onEnter={handleEnter} />
          ) : (
            <div>
              <Navbar />
              <CardsWrapper />
              <GrainEffect />
            </div>
          )}
        </div>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
