import React, { useState } from 'react';
import NavbarLeft from './components/navbar/navbarlist';
import TransitionPage from './components/TransitionPage';
import CardsWrapper from './components/CardsWrapper'; // Si vous voulez aussi inclure CardsWrapper
const MainComponent = () => {
  const [selectedCard, setSelectedCard] = useState(null);

  const handleSelectCard = (card) => {
    console.log('Card selected:', card); // Debugging line
    setSelectedCard(card);
  };

  const handleBack = () => {
    setSelectedCard(null);
  };

  return (
    <div>
      <NavbarLeft onSelectCard={handleSelectCard} />

      {!selectedCard && (
        <div style={{ marginLeft: '200px' }}>
          <CardsWrapper setBackgroundColor={(color) => document.body.style.backgroundColor = color} />
        </div>
      )}

      {selectedCard && (
        <TransitionPage
          selectedCard={selectedCard}
          onBack={handleBack}
        />
      )}
    </div>
  );
};

export default MainComponent;
