import React, { useEffect, useState, useContext } from 'react';
import styled from 'styled-components';
import { UserContext } from './UserContext'; // Import the context

const Modal = ({ isOpen, onClose, onSave }) => {
  const { firstName1, firstName2, selectedToys, updateUserPreferences } = useContext(UserContext);
  const [toys, setToys] = useState([]);
  const [formState, setFormState] = useState({
    firstName1: firstName1 || '',
    firstName2: firstName2 || '',
    selectedToys: selectedToys || [],
  });

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await fetch('http://localhost:1812/api/toys');
        const data = await response.json();
        setToys(data);
      } catch (error) {
        console.error('Error fetching toys:', error);
      }
    };

    fetchToys();
  }, []);

  const handleChange = (e) => {
    setFormState({
      ...formState,
      [e.target.name]: e.target.value,
    });
  };

  const toggleToySelection = (toy) => {
    setFormState((prev) => ({
      ...prev,
      selectedToys: prev.selectedToys.includes(toy)
        ? prev.selectedToys.filter(t => t !== toy)
        : [...prev.selectedToys, toy],
    }));
  };

  const handleSave = () => {
    updateUserPreferences(formState); // Update via context
    onSave(formState);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Personnaliser votre expÃ©rience</SectionTitle>
        <ColumnContainer>
          <SmallColumnContainer>
            <SubTitle>Ã€ qui avons nous l'honneur ?</SubTitle>
            <InputField
              type="text"
              name="firstName1"
              placeholder="Femme"
              value={formState.firstName1}
              onChange={handleChange}
            />
            <InputField
              type="text"
              name="firstName2"
              placeholder="Homme"
              value={formState.firstName2}
              onChange={handleChange}
            />
          </SmallColumnContainer>
          <SmallColumnContainer>
            <SubTitle>Qu'est ce qu'il y'a au menu ?</SubTitle>
            <ToyGrid>
              {toys.map((toy, index) => (
                <ToyButton
                  key={index}
                  isSelected={formState.selectedToys.includes(toy.name)}
                  onClick={() => toggleToySelection(toy.name)}
                >
                  {toy.name}
                </ToyButton>
              ))}
            </ToyGrid>
          </SmallColumnContainer>
        </ColumnContainer>

        <ButtonContainer>
          <Button onClick={handleSave}>
            <span role="img" aria-label="Tour">ðŸ‘¤</span> Confirmer 
          </Button>
        </ButtonContainer>
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
