import React, { useEffect } from 'react';
import Lottie from 'lottie-react';
import animationData from '../path/to/your/lottie/file.json';

const LottieAnimation = ({ isVisible }) => {
  useEffect(() => {
    console.log('LottieAnimation mounted');
  }, []);

  useEffect(() => {
    console.log('isVisible changed:', isVisible);
  }, [isVisible]);

  if (!isVisible) {
    console.log('LottieAnimation is not visible');
    return null;
  }

  console.log('LottieAnimation is visible');

  return (
    <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', zIndex: 1000, backgroundColor: '#fff' }}>
      <Lottie animationData={animationData} loop={false} />
    </div>
  );
};

export default LottieAnimation;
