import React, { useState } from 'react';
import Button from './navitem';
import { FaKey, FaEye } from 'react-icons/fa'; 
import Modal from '../Modal';

const NavbarRight = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [selectedToys, setSelectedToys] = useState([]);
  const [formData, setFormData] = useState({ firstName1: '', firstName2: '' }); // Assurez-vous que setFormData est défini ici

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const handleSave = (data) => {
    setSelectedToys(data.selectedToys);
    setFormData({
      firstName1: data.firstName1,
      firstName2: data.firstName2,
    });
    closeModal();
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', position: 'absolute', right: '48px', top: '48px', gap: '10px' }}>
      <Button icon={<FaKey />} label="Filters" onClick={openModal} />
      <Modal 
        isOpen={isModalOpen} 
        onClose={closeModal} 
        onSave={handleSave} 
        selectedToys={selectedToys}
        formData={formData}
        setFormData={setFormData}  // Passez setFormData correctement
      />
    </div>
  );
};

export default NavbarRight;
