import React, { useState } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const FullScreenCover = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 9999;
  pointer-events: none;
  background-color: transparent;
`;

const SvgPath = styled.svg`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
`;

const CardsWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px;
  padding: 50px;
  height: 100vh;
`;

const Card = styled.div`
  width: 300px;
  height: 450px;
  background-color: #f0f0f0;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  cursor: pointer;
`;

const App = () => {
  const [isAnimating, setIsAnimating] = useState(false);
  const pathRef = useRef(null);

  const handleClick = () => {
    if (!isAnimating) {
      setIsAnimating(true);
      const pathLength = pathRef.current.getTotalLength();

      gsap.set(pathRef.current, {
        strokeDasharray: pathLength,
        strokeDashoffset: pathLength,
      });

      gsap.to(pathRef.current, {
        strokeDashoffset: 0,
        duration: 3,
        ease: 'power2.inOut',
        onComplete: () => {
          console.log('Animation completed');
        },
      });
    }
  };

  return (
    <>
      <FullScreenCover>
        <SvgPath xmlns="http://www.w3.org/2000/svg" viewBox="0 0 2291 1821">
          <path
            ref={pathRef}
            d="M495 1102L1125.81 1328.97C1267.93 1380.11 1425.68 1362.92 1553.45 1282.37L1633.82 1231.7C1863.66 1086.8 1895.52 764.218 1698.48 577.15V577.15C1556.36 442.229 1339.43 424.591 1177.38 534.782L1045.86 624.217C865.688 746.732 634.084 764.587 437.266 671.133L199 558"
            stroke="#77FFEE"
            strokeWidth="1200"  // Augmentez cette valeur pour couvrir tout l'écran
            fill="none"
          />
        </SvgPath>
      </FullScreenCover>
      <CardsWrapper>
        <Card onClick={handleClick} />
        <Card onClick={handleClick} />
        <Card onClick={handleClick} />
      </CardsWrapper>
    </>
  );
};

export default App;
