import React, { useRef, useState } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  overflow: hidden;
`;

const CardsContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const CardWrapper = styled.div`
  perspective: 1000px;
  width: 392px;
  height: 548px;
  position: absolute;
  cursor: pointer;
`;

const CardInner = styled.div`
  width: 100%;
  height: 100%;
  transition: transform 1s;
  transform-style: preserve-3d;
  position: relative;
`;

const CardFace = styled.div`
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  position: absolute;
  backface-visibility: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
`;

const CardFront = styled(CardFace)`
  background-image: url(${props => props.image});
`;

const CardBack = styled(CardFace)`
  background-color: white;
  transform: rotateY(180deg);
`;

const CardText = styled.div`
  color: #333;
  font-family: 'Poppins', sans-serif;
  font-size: 24px;
  text-align: center;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ActionVerite = ({ firstName1, firstName2, selectedToys }) => {
  const [currentPlayer, setCurrentPlayer] = useState(firstName1);
  const [clickedCard, setClickedCard] = useState(null);
  const [randomText, setRandomText] = useState('');
  const leftCardRef = useRef(null);
  const rightCardRef = useRef(null);

  const fetchRandomActionOrTruth = async (type) => {
    try {
      const toysParam = selectedToys?.length ? selectedToys.join(',') : '';
      const response = await axios.get(`http://localhost:1812/api/truthordare/random`, {
        params: { type: type, toys: toysParam }
      });
      console.log('API response:', response.data); // Debugging info

      if (response.data && response.data.template) {
        return response.data.template;
      } else {
        console.error('Invalid response format:', response.data);
        return 'No data available';
      }
    } catch (error) {
      console.error(`Error fetching random ${type}:`, error);
      return 'Error loading text';
    }
  };

  const handleCardClick = async (card) => {
    try {
      setClickedCard(card);

      if (card === 'left') {
        const randomTruth = await fetchRandomActionOrTruth('truth');
        setRandomText(randomTruth);

        gsap.to(leftCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
        gsap.to(rightCardRef.current, { duration: 0.8, x: 1000, opacity: 0 });

        setTimeout(() => {
          gsap.to(leftCardRef.current.querySelector('.inner'), { rotationY: 180 });
        }, 500); // Delay before flipping
      } else {
        const randomAction = await fetchRandomActionOrTruth('action');
        setRandomText(randomAction);

        gsap.to(rightCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
        gsap.to(leftCardRef.current, { duration: 0.8, x: -1000, opacity: 0 });

        setTimeout(() => {
          gsap.to(rightCardRef.current.querySelector('.inner'), { rotationY: 180 });
        }, 500); // Delay before flipping
      }
    } catch (error) {
      console.error('Error handling card click:', error);
    }
  };

  const resetCards = () => {
    setClickedCard(null);
    setCurrentPlayer(currentPlayer === firstName1 ? firstName2 : firstName1);
    gsap.to(leftCardRef.current.querySelector('.inner'), { rotationY: 0 });
    gsap.to(rightCardRef.current.querySelector('.inner'), { rotationY: 0 });
    gsap.to(leftCardRef.current, { duration: 0.8, x: '-150px', rotation: -10, scale: 1, opacity: 1 });
    gsap.to(rightCardRef.current, { duration: 0.8, x: '150px', rotation: 10, scale: 1, opacity: 1 });
  };

  return (
    <Container>
      <CardsContainer>
        <CardWrapper ref={leftCardRef} onClick={() => handleCardClick('left')}>
          <CardInner className="inner">
            <CardFront image={require('../images/junebaby.png')} />
            <CardBack>
              <CardText>{clickedCard === 'left' && randomText}</CardText>
            </CardBack>
          </CardInner>
        </CardWrapper>
        <CardWrapper ref={rightCardRef} onClick={() => handleCardClick('right')}>
          <CardInner className="inner">
            <CardFront image={require('../images/junebaby.png')} />
            <CardBack>
              <CardText>{clickedCard === 'right' && randomText}</CardText>
            </CardBack>
          </CardInner>
        </CardWrapper>
      </CardsContainer>
      <ButtonContainer>
        <Button onClick={resetCards}>
          <span role="img" aria-label="Tour">👤</span> Au tour de {currentPlayer}
        </Button>
        <Button>
          <span role="img" aria-label="I love it">❤️</span> I love it
        </Button>
      </ButtonContainer>
    </Container>
  );
};

export default ActionVerite;
