import React, { useState } from 'react';
import styled from 'styled-components';
import lowIntensityImage from '../images/logo-5.svg';

const ImageWrapper = styled.div`
  position: relative;
  width: 200px;
  height: 200px;
  background-image: url(${lowIntensityImage});
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  z-index: 1; /* Ajout du z-index ici pour l'image de fond */
`;

const FillOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(255, 0, 0, 0.5); /* Couleur de remplissage */
  clip-path: inset(100% 0 0 0);
  transition: clip-path 2s ease-in-out;
  z-index: 2; /* Ajout du z-index ici pour l'overlay */
  &.fill-animation {
    clip-path: inset(0 0 0 0);
  }
`;

const ActionVerite = () => {
  const [isFilled, setIsFilled] = useState(false);

  const startAnimation = () => {
    setIsFilled(true);
  };

  return (
    <div>
      <ImageWrapper>
        <FillOverlay className={isFilled ? 'fill-animation' : ''} />
      </ImageWrapper>
      <button onClick={startAnimation}>Start Animation</button>
    </div>
  );
};

export default ActionVerite;
