import React from 'react';
import styled from 'styled-components';
import HomeCard from './HomeCard';

// Import des images
import juneBabyImg from '../images/junebaby.png';
import loveImg from '../images/love.png';
import yourselfImg from '../images/yourself.png';

const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px;
  padding: 50px;
`;

const CardsWrapper = ({ setBackgroundColor }) => {
  return (
    <Wrapper>
      <HomeCard
        image={juneBabyImg} // Utilisation de l'import pour l'image
        rotate="-5deg"
        onMouseEnter={() => setBackgroundColor('#FFD700')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
      />
      <HomeCard
        image={yourselfImg} // Utilisation de l'import pour l'image
        rotate="0deg"
        onMouseEnter={() => setBackgroundColor('#FF69B4')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
      />
      <HomeCard
        image={loveImg} // Utilisation de l'import pour l'image
        rotate="5deg"
        onMouseEnter={() => setBackgroundColor('#FF4500')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
      />
    </Wrapper>
  );
};

export default CardsWrapper;
