import React, { useState, useEffect } from 'react';
import LottieAnimation from './LottieAnimation';
import ActionVerite from './ActionVerite';
import Roleplay from './Roleplay';
import { useCard } from './CardContext';

const TransitionPage = ({ onBack }) => {
  const { selectedCard, isLottieVisible, finishAnimation } = useCard();
  const [showNewContent, setShowNewContent] = useState(false);

  useEffect(() => {
    if (!isLottieVisible && selectedCard) {
      const timer = setTimeout(() => {
        setShowNewContent(true);
      }, 1000); // Délai de 1 seconde après la fin de l'animation

      return () => clearTimeout(timer); // Nettoyage du timer
    }
  }, [isLottieVisible, selectedCard]);

  const getLottieData = () => {
    switch (selectedCard) {
      case 'card1':
        return require('../test.json'); // Chemin correct vers le fichier Lottie
      case 'card2':
        return require('../test.json'); // Chemin correct vers le fichier Lottie
      case 'card3':
        return require('../test.json'); // Chemin correct vers le fichier Lottie
      default:
        return null;
    }
  };

  const getNewContent = () => {
    if (!showNewContent) return null;

    switch (selectedCard) {
      case 'card1':
        return <ActionVerite />;
      case 'card2':
        return <div>New content for Card 2</div>;
      case 'card3':
        return <Roleplay />;
      default:
        return null;
    }
  };

  return (
    <div>
      {isLottieVisible && (
        <div style={{ position: 'relative' }}>
          <LottieAnimation
            animationData={getLottieData()}
            onComplete={finishAnimation}
          />
          {/* Rendre la carte initiale visible sous l'animation */}
          <div style={{ zIndex: 0, position: 'relative' }}>
            {getNewContent()}
          </div>
        </div>
      )}
      {!isLottieVisible && showNewContent && (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2em', color: '#333', flexDirection: 'column' }}>
          {getNewContent()} {/* Affiche le nouveau contenu après l'animation */}
          <button onClick={onBack}>Back</button>
        </div>
      )}
    </div>
  );
};

export default TransitionPage;
