import React, { useEffect } from 'react';
import Lottie from 'lottie-react';
import styled from 'styled-components';

const LottieWrapper = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: transparent;
  z-index: 9999;
`;

const LottieAnimation = ({ animationData, onComplete }) => {
  useEffect(() => {
    console.log('LottieAnimation mounted');

    return () => {
      console.log('LottieAnimation unmounted');
    };
  }, []);

  return (
    <LottieWrapper>
      <Lottie
        animationData={animationData}
        loop={false}
        onComplete={onComplete ? () => onComplete() : null}
        style={{ width: '100%', height: '100%' }} // Ajuster la taille en fonction de l'écran
      />
    </LottieWrapper>
  );
};

export default LottieAnimation;
