import React, { useRef } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import Logo from './components/Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';
import CardRenderer from './components/CardRenderer'; 
import NavbarRight from './components/navbar/navbarright'; // Assurez-vous d'importer correctement

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const CardsContainer = styled.div`
  opacity: ${({ hasEntered }) => (hasEntered ? 1 : 0)};
  transform: translateY(50px);
  transition: opacity 1s ease, transform 1s ease;
`;

const MainComponentContent = () => {
  const { selectedCard } = useCard();
  const [animateLogo, setAnimateLogo] = React.useState(false);
  const [backgroundColor, setBackgroundColor] = React.useState('#FFFFFF'); 
  const [hasEntered, setHasEntered] = React.useState(false);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);
    setHasEntered(true);

    gsap.to(cardsRef.current, {
      opacity: 1,
      y: 0,
      duration: 1,
      ease: 'power2.out',
      delay: 0.2,
    });
  };

  const resetAnimation = () => {
    gsap.fromTo(
      cardsRef.current,
      { opacity: 0, y: 50 },
      { opacity: 1, y: 0, duration: 1, ease: 'power2.out' }
    );
  };

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <Logo animate={animateLogo} />
      <Navbar />

      {/* Passez la fonction resetAnimation Ã  NavbarRight */}
      <NavbarRight onResetAnimation={resetAnimation} />

      {!selectedCard && (
        <CardsContainer ref={cardsRef} hasEntered={hasEntered}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}
    </BackgroundContainer>
  );
};

const MainComponent = () => {
  return (
    <CardProvider>
      <PageProvider>
        <MainComponentContent />
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
