import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import NavButton from './navbar/navbarbutton'

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
  margin-right:64px;
  line-height:50px
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 16px;
`;

const ToyGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const ToyButton = styled.button`
  background: ${props => props.isSelected ? '#ff4500' : '#fff'};
  border: 1px solid ${props => props.isSelected ? '#ff4500' : '#ddd'};
  color: ${props => props.isSelected ? '#fff' : '#000'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
    border-color: #ff4500;
  }
`;

const ColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const SmallColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0px;
`;

const InputField = styled.input`
  padding: 10px;
  margin-bottom: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Modal = ({ isOpen, onClose, onSave, selectedToys, formData, setFormData }) => {
  const [toys, setToys] = useState([]);
  const [selectedToysState, setSelectedToysState] = useState(selectedToys || []);

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await fetch('http://localhost:1812/api/toys');
        const data = await response.json();
        setToys(data);
      } catch (error) {
        console.error('Error fetching toys:', error);
      }
    };

    fetchToys();

    // R√©cup√©rer les donn√©es sauvegard√©es dans le localStorage
    const savedPreferences = localStorage.getItem('userPreferences');
    if (savedPreferences) {
      const savedData = JSON.parse(savedPreferences);
      if (setFormData) {  // V√©rification que setFormData est pass√©
        setFormData({
          firstName1: savedData.firstName1 || '',
          firstName2: savedData.firstName2 || '',
        });
        setSelectedToysState(savedData.selectedToys || []);
      } else {
        console.error('setFormData is not defined');
      }
    }
  }, [setFormData]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const toggleToySelection = (toy) => {
    setSelectedToysState(prevSelectedToys =>
      prevSelectedToys.includes(toy)
        ? prevSelectedToys.filter(t => t !== toy)
        : [...prevSelectedToys, toy]
    );
  };

  const handleSave = () => {
    const dataToSave = { ...formData, selectedToys: selectedToysState };

    // Sauvegarder les donn√©es dans le localStorage
    localStorage.setItem('userPreferences', JSON.stringify(dataToSave));

    // Appeler la fonction onSave avec les donn√©es sauvegard√©es
    onSave(dataToSave);
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Personnaliser votre exp√©rience</SectionTitle>
        <ColumnContainer>
        <SmallColumnContainer>
        <SubTitle>√Ä qui avons nous l'honneur ?</SubTitle>
          <InputField
            type="text"
            name="firstName1"
            placeholder="Femme"
            value={formData.firstName1 || ''}
            onChange={handleChange}
          />
          <InputField
            type="text"
            name="firstName2"
            placeholder="Homme"
            value={formData.firstName2 || ''}
            onChange={handleChange}
          />
                    </SmallColumnContainer>
          <SmallColumnContainer>
            <SubTitle>Qu'est ce qu'il y'a au menu ?</SubTitle>
            <ToyGrid>
              {toys.map((toy, index) => (
                <ToyButton
                  key={index}
                  isSelected={selectedToysState.includes(toy.name)}
                  onClick={() => toggleToySelection(toy.name)}
                >
                  {toy.name}
                </ToyButton>
              ))}
            </ToyGrid>
          </SmallColumnContainer>
        </ColumnContainer>

        <Button onClick={handleChange}>
          <span role="img" aria-label="Tour">üë§</span> Joueur suivant
        </Button>

      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
