import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const ModalOverlay = styled.div`
  /* Styles pour le fond du modal */
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  /* Styles pour le contenu du modal */
  background: #fff;
  border-radius: 20px;
  padding: 40px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  /* Styles pour le bouton de fermeture */
  position: absolute;
  top: 20px;
  right: 20px;
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  /* Styles pour les titres des sections */
  font-size: 32px;
  font-weight: bold;
  color: #000;
  margin-bottom: 20px;
`;

const SubTitle = styled.h3`
  /* Styles pour les sous-titres */
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 10px;
`;

const ToyGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const ToyButton = styled.button`
  background: #fff;
  border: 1px solid #ddd;
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;

  &:hover {
    background: #f0f0f0;
  }
`;

const Modal = ({ isOpen, onClose }) => {
  const [toys, setToys] = useState([]);

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await axios.get('http://localhost:1812/api/toys'); // Remplacez par l'URL correcte de votre API
        setToys(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des toys:', error);
      }
    };

    fetchToys();
  }, []);

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Modify your experience</SectionTitle>

        <SubTitle>Select your toys</SubTitle>
        <ToyGrid>
          {toys.map((toy, index) => (
            <ToyButton key={index}>{toy.name}</ToyButton>
          ))}
        </ToyGrid>
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
