import React, { useState } from 'react';
import HomeCard from './HomeCard';
import ActionVerite from './ActionVerite'; // Assurez-vous d'importer le composant ActionVerite

const CardsWrapper = ({ setBackgroundColor }) => {
  const [selectedCard, setSelectedCard] = useState(null);
  const defaultBackgroundColor = '#FBF8F1'; // Couleur de fond par défaut

  const handleCardClick = (card) => {
    console.log('Card clicked:', card);
    setSelectedCard(card); // Affiche le composant ActionVerite
  };

  const handleMouseEnter = (color) => {
    setBackgroundColor(color); // Change la couleur de fond au survol
  };

  const handleMouseLeave = () => {
    setBackgroundColor(defaultBackgroundColor); // Remet la couleur de fond par défaut
  };

  if (selectedCard) {
    return <ActionVerite card={selectedCard} />; // Affiche le composant ActionVerite lorsqu'une carte est cliquée
  }

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      width: '100vw',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '20px',
      padding: '50px'
    }}>
      <HomeCard
        image={require('../images/love.png')}
        rotate="-5deg"
        onMouseEnter={() => handleMouseEnter('#FFD700')}
        onMouseLeave={handleMouseLeave}
        onClick={() => handleCardClick('actionVerite')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(-5deg)',
          backgroundColor: defaultBackgroundColor,
        }}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="0deg"
        onMouseEnter={() => handleMouseEnter('#FF69B4')}
        onMouseLeave={handleMouseLeave}
        onClick={() => handleCardClick('generator')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(0deg)',
          backgroundColor: defaultBackgroundColor,
        }}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="5deg"
        onMouseEnter={() => handleMouseEnter('#FF4500')}
        onMouseLeave={handleMouseLeave}
        onClick={() => handleCardClick('roleplay')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(5deg)',
          backgroundColor: defaultBackgroundColor,
        }}
      />
    </div>
  );
};

export default CardsWrapper;
