import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardTransitionPage from './components/CardTransitionPage';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import LoadingScreen from './components/LoadingScreen';
import Logo from './components/Logo';

const MainComponent = () => {
  const [loading, setLoading] = useState(true);
  const [backgroundColor, setBackgroundColor] = useState('#faf3d9'); // Initial background color

  const handleEnter = () => {
    setLoading(false);
  };

  return (
    <CardProvider>
      <PageProvider>
        <div style={{ backgroundColor }}>
          <Logo size="300px" top="50%" left="50%" />
          {loading && <LoadingScreen onEnter={handleEnter} />}
          {!loading && (
            <div>
              <Navbar />
              <CardsWrapper setBackgroundColor={setBackgroundColor} /> {/* Passing the function */}
              <GrainEffect />
            </div>
          )}
        </div>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
