import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';
import axios from 'axios';
import lowIntensityImage from '../images/logo.svg';
import mediumIntensityImage from '../images/logo.svg';
import highIntensityImage from '../images/logo.svg';
import GrainEffect from './GrainEffect';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  overflow: hidden;
  background-color: ${({ intensity }) => 
    intensity === 'low' ? '#FBF8F1' : 
    intensity === 'medium' ? '#D51C2C' : 
    '#5A0C13'};
  background-image: ${({ intensity }) => 
    intensity === 'low' ? `url(${lowIntensityImage})` : 
    intensity === 'medium' ? `url(${mediumIntensityImage})` : 
    `url(${highIntensityImage})`};
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  position: relative;
  z-index: 1;
`;

const CardsContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 5;
`;

const CardWrapper = styled.div`
  perspective: 1000px;
  width: 392px;
  height: 548px;
  position: absolute;
  cursor: pointer;
  z-index: 5;
`;

const Footer = styled.div`
  font-family: 'Poppins', sans-serif;
  text-transform: uppercase;
  font-size: 14px;
  font-weight: 400;
  color: #000000;
  margin-top: 20px;
`;

const CardInner = styled.div`
  width: 100%;
  height: 100%;
  transition: transform 1s;
  transform-style: preserve-3d;
  position: relative;
`;

const CardFace = styled.div`
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  position: absolute;
  backface-visibility: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
`;

const CardFront = styled(CardFace)`
  background-image: url(${props => props.image});
`;

const Title = styled.h1`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  margin-bottom: 20px;
`;

const CardBack = styled(CardFace)`
  background-color: white;
  transform: rotateY(180deg);
  display:flex;
  flex-direction:column;
  padding: 48px;
  justify-content: space-between;
`;

const CardText = styled.div`
  color: #333;
  font-family: Paragon;
  font-size: 1.75rem;
  text-align: center;
`;

const ButtonContainer = styled.div`
  position:absolute;
  bottom : 48px;
  display: flex;
  gap: 20px;
  margin-top: 20px;
  z-index: 5;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const TimerButton = styled(Button)`
  background-color: #ff4500;
  color: white;

  &:hover {
    background-color: #ff5733;
  }
`;

const ActionVerite = () => {
  const [clickedCard, setClickedCard] = useState(null);
  const [randomText, setRandomText] = useState('');
  const [duration, setDuration] = useState(null); // Ã‰tat pour la durÃ©e du gage
  const [remainingTime, setRemainingTime] = useState(null); // Ã‰tat pour le temps restant
  const [isPaused, setIsPaused] = useState(false); // Ã‰tat pour savoir si le timer est en pause
  const [intervalId, setIntervalId] = useState(null); // Stocker l'ID de l'intervalle pour le timer
  const [showTimerButton, setShowTimerButton] = useState(false); // Ã‰tat pour gÃ©rer l'affichage du bouton du timer
  const [selectedToys, setSelectedToys] = useState([]);
  const leftCardRef = useRef(null);
  const rightCardRef = useRef(null);

  const [currentPlayer, setCurrentPlayer] = useState('');
  const [firstName1, setFirstName1] = useState('');
  const [firstName2, setFirstName2] = useState('');

  useEffect(() => {
    const savedPreferences = localStorage.getItem('userPreferences');
    if (savedPreferences) {
      const { firstName1, firstName2, selectedToys } = JSON.parse(savedPreferences);
      console.log('Preferences loaded from localStorage:', { firstName1, firstName2, selectedToys });
      setFirstName1(firstName1 || '');
      setFirstName2(firstName2 || '');
      setSelectedToys(selectedToys || []);
      setCurrentPlayer(firstName1 || '');
    }
  }, []);

  const [intensity, setIntensity] = useState('low'); // 'low', 'medium', 'high'

  const changeIntensity = () => {
    setIntensity(prevIntensity => {
      let newIntensity;
      if (prevIntensity === 'low') {
        newIntensity = 'medium';
      } else if (prevIntensity === 'medium') {
        newIntensity = 'high';
      } else {
        newIntensity = 'low';
      }

      console.log(`Intensity changed from ${prevIntensity} to ${newIntensity}`);
      return newIntensity;
    });
  };

  useEffect(() => {
    gsap.set(leftCardRef.current, { x: '-150px', rotation: -10 });
    gsap.set(rightCardRef.current, { x: '150px', rotation: 10 });
    console.log(`Background updated to ${intensity} intensity`);
  }, [intensity]);

  const fetchRandomActionOrTruth = async (type, player) => {
    try {
      const mappedPlayer = player === firstName1 ? 'firstName1' : 'firstName2';
      const toysParam = selectedToys?.length ? [...selectedToys, 'all'] : ['all'];
      console.log('Fetching with type:', type, 'toys:', toysParam.join(','), 'player:', mappedPlayer, 'intensity:', intensity);

      const response = await axios.get(`http://localhost:1812/api/truthordare/random`, {
        params: { type: type, player: mappedPlayer, toys: toysParam.join(','), intensity: intensity }
      });

      console.log('Raw API response:', response);
      console.log('API response data:', response.data);

      if (response.data && response.data.template) {
        const { template, duration } = response.data;
        setDuration(duration || null); // Mettre Ã  jour la durÃ©e si elle est prÃ©sente
        setRemainingTime(duration); // Initialiser le temps restant
        return template;
      } else {
        console.error('Invalid response format:', response.data);
        return 'No data available';
      }
    } catch (error) {
      console.error(`Error fetching random ${type}:`, error);
      return 'Error loading text';
    }
  };

  const handleCardClick = async (card) => {
    if (clickedCard) return;

    try {
      let randomText = '';

      console.log(`Current player: ${currentPlayer}`);
      console.log(`firstName1 = ${firstName1}`);
      console.log(`firstName2 = ${firstName2}`);
      console.log(`Selected toys:`, selectedToys);

      if (card === 'left') {
        randomText = await fetchRandomActionOrTruth('truth', currentPlayer);
      } else {
        randomText = await fetchRandomActionOrTruth('dare', currentPlayer);
      }

      setRandomText(randomText);
      setClickedCard(card);
      setShowTimerButton(!!duration); // Affiche le bouton du timer si une durÃ©e est prÃ©sente

      if (card === 'left') {
        gsap.to(leftCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
        gsap.to(rightCardRef.current, { duration: 0.8, x: 1000, opacity: 0 });
        setTimeout(() => {
          gsap.to(leftCardRef.current.querySelector('.inner'), { rotationY: 180 });
        }, 500);
      } else {
        gsap.to(rightCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
        gsap.to(leftCardRef.current, { duration: 0.8, x: -1000, opacity: 0 });
        setTimeout(() => {
          gsap.to(rightCardRef.current.querySelector('.inner'), { rotationY: 180 });
        }, 500);
      }
    } catch (error) {
      console.error('Error handling card click:', error);
    }
  };

  const resetCards = () => {
    setClickedCard(null);
    setRandomText('');
    setDuration(null); // RÃ©initialiser la durÃ©e
    setRemainingTime(null); // RÃ©initialiser le temps restant
    setIsPaused(false); // RÃ©initialiser l'Ã©tat de pause
    clearInterval(intervalId); // ArrÃªter le timer
    setShowTimerButton(false); // Masquer le bouton du timer
    setCurrentPlayer(prevPlayer => prevPlayer === firstName1 ? firstName2 : firstName1);
    gsap.to(leftCardRef.current.querySelector('.inner'), { rotationY: 0 });
    gsap.to(rightCardRef.current.querySelector('.inner'), { rotationY: 0 });
    gsap.to(leftCardRef.current, { duration: 0.8, x: '-150px', rotation: -10, scale: 1, opacity: 1 });
    gsap.to(rightCardRef.current, { duration: 0.8, x: '150px', rotation: 10, scale: 1, opacity: 1 });
  };

  const startTimer = () => {
    if (!remainingTime) return;

    if (isPaused) {
      setIsPaused(false);
      return;
    }

    const id = setInterval(() => {
      setRemainingTime(prevTime => {
        if (prevTime <= 1) {
          clearInterval(id);
          setIsPaused(true);
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    setIntervalId(id);
  };

  const pauseTimer = () => {
    setIsPaused(true);
    clearInterval(intervalId);
  };

  const resetTimer = () => {
    clearInterval(intervalId);
    setRemainingTime(duration);
    setIsPaused(false);
    startTimer();
  };

  const handleTimerButtonClick = () => {
    if (remainingTime === 0) {
      resetTimer();
    } else if (isPaused) {
      startTimer();
    } else {
      pauseTimer();
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
  };

  return (
    <Container intensity={intensity}>
      <CardsContainer>
        <CardWrapper
          ref={leftCardRef}
          onClick={() => handleCardClick('left')}
        >
          <CardInner className="inner">
            <CardFront image={require('../images/junebaby.png')} />
            <CardBack>
              <Title>Truth & Dare</Title>
              <CardText>{clickedCard === 'left' && randomText ? `${currentPlayer}, ${randomText}` : currentPlayer}</CardText>
              <Footer>LoveTogether</Footer>
            </CardBack>
          </CardInner>
        </CardWrapper>
        <CardWrapper
          ref={rightCardRef}
          onClick={() => handleCardClick('right')}
        >
          <CardInner className="inner">
            <CardFront image={require('../images/love.png')} />
            <CardBack>
              <Title>Truth & Dare</Title>
              <CardText>{clickedCard === 'right' && randomText ? `${currentPlayer}, ${randomText}` : currentPlayer}</CardText>
              <Footer>LoveTogether</Footer>
            </CardBack>
          </CardInner>
        </CardWrapper>
      </CardsContainer>
      <ButtonContainer>
        {clickedCard ? (
          <>
            <Button onClick={resetCards}>
              <span role="img" aria-label="Tour">ðŸ‘¤</span> Joueur suivant
            </Button>
            {showTimerButton && (
              <TimerButton onClick={handleTimerButtonClick}>
                {remainingTime === 0 ? 'Recommencer' : isPaused ? 'Reprendre' : `Pause (${formatTime(remainingTime)})`}
              </TimerButton>
            )}
          </>
        ) : (
          <Button onClick={() => { 
            console.log('Turn button clicked');
            handleCardClick(null); // Fonction pour gÃ©rer le tour
          }}>
            <span role="img" aria-label="Tour">ðŸ‘¤</span> Ã€ ton tour, {currentPlayer}
          </Button>
        )}
        {!clickedCard && (
          <Button onClick={() => { 
            console.log('Intensity button clicked');
            changeIntensity();
          }}>
            <span role="img" aria-label="Tour">ðŸ‘¤</span>{intensity.charAt(0).toUpperCase() + intensity.slice(1)}
          </Button>
        )}
      </ButtonContainer>

      <GrainEffect />

    </Container>
  );
};

export default ActionVerite;
