import React, { useRef, useState } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  overflow: hidden;
`;

const CardsContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Card = styled.div`
  width: 392px;
  height: 548px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  position: absolute;
  cursor: pointer;
  transform: ${props => (props.isLeft ? 'translateX(-220px) rotate(-10deg)' : 'translateX(220px) rotate(10deg)')};
  transition: transform 0.6s cubic-bezier(0.22, 1, 0.36, 1);
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ActionVerite = () => {
  const [clickedCard, setClickedCard] = useState(null);
  const [leftImage, setLeftImage] = useState(require('../images/junebaby.png'));
  const [rightImage, setRightImage] = useState(require('../images/junebaby.png'));
  const leftCardRef = useRef(null);
  const rightCardRef = useRef(null);

  const handleCardClick = (card) => {
    setClickedCard(card);
    if (card === 'left') {
      gsap.to(leftCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
      gsap.to(rightCardRef.current, { duration: 0.8, x: 1000, opacity: 0 });
      setTimeout(() => {
        setLeftImage(require('../images/love.png')); // Change to the desired new image after delay
      }, 1000); // 2000ms delay (2 seconds)
    } else {
      gsap.to(rightCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
      gsap.to(leftCardRef.current, { duration: 0.8, x: -1000, opacity: 0 });
      setTimeout(() => {
        setRightImage(require('../images/love.png')); // Change to the desired new image after delay
      }, 2000); // 2000ms delay (2 seconds)
    }
  };

  const resetCards = () => {
    setClickedCard(null);
    setLeftImage(require('../images/junebaby.png')); // Reset to original image
    setRightImage(require('../images/junebaby.png')); // Reset to original image
    gsap.to(leftCardRef.current, { duration: 0.8, x: '-220px', rotation: -10, scale: 1, opacity: 1 });
    gsap.to(rightCardRef.current, { duration: 0.8, x: '220px', rotation: 10, scale: 1, opacity: 1 });
  };

  return (
    <Container>
      <CardsContainer>
        <Card
          ref={leftCardRef}
          image={leftImage}
          isLeft
          onClick={() => handleCardClick('left')}
        />
        <Card
          ref={rightCardRef}
          image={rightImage}
          onClick={() => handleCardClick('right')}
        />
      </CardsContainer>
      <ButtonContainer>
        <Button onClick={resetCards}>
          <span role="img" aria-label="Tour">üë§</span> Au tour de William
        </Button>
        <Button>
          <span role="img" aria-label="I love it">‚ù§Ô∏è</span> I love it
        </Button>
      </ButtonContainer>
    </Container>
  );
};

export default ActionVerite;
