import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
  margin-right: 64px;
  line-height: 50px;
`;

const SubTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  color: #333;
  margin-bottom: 0px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

const GroupContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

const Input = styled.input`
  padding: 10px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: 100%;
`;

const Textarea = styled.textarea`
  padding: 10px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
  min-height: 100px;
  resize: vertical;
`;

const Select = styled.select`
  padding: 10px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  flex-grow:2;
  align-text:center;
justify-content:center;
  padding: 10px 20px;
  background-color: black;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: white;
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;
  width: fit-content;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 12px;
`;

const ChipsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const Chip = styled.button`
  background: ${props => props.selected ? '#ff4500' : '#f0f0f0'};
  color: ${props => props.selected ? '#fff' : '#000'};
  border: 2px solid ${props => props.selected ? '#ff4500' : '#ddd'};
  border-radius: 20px;
  padding: 8px 16px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
  flex-shrink: 0;

  &:hover {
    background: #ff4500;
    color: #fff;
  }
`;

const SelectWrapper = styled.div`
  position: relative;
  width: 100%;
`;

const StyledSelect = styled.div`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;
  background-color: white;
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:after {
    content: 'â–¼';
    font-size: 12px;
  }
`;

const DropdownList = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  max-height: 200px;
  overflow-y: auto;
  background-color: white;
  border: 1px solid #ccc;
  border-top: none;
  border-radius: 0 0 4px 4px;
  z-index: 1000;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  padding: 8px 12px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    background-color: #f5f5f5;
  }
`;

const AddTruthOrDareModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    type: 'truth',
    player: 'all',
    template: '',
    duration: '',
    intensity: [],
    toys: [],
  });

  const [availableToys, setAvailableToys] = useState([]);
  const [toySearchTerm, setToySearchTerm] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const durationOptions = [
    { label: '30s', value: 30 },
    { label: '1m', value: 60 },
    { label: '2m', value: 120 },
    { label: '3m', value: 180 },
    { label: '5m', value: 300 },
  ];

  const intensityOptions = ['low', 'medium', 'high'];

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await axios.get('http://localhost:1812/api/toys');
        setAvailableToys(response.data);
      } catch (error) {
        console.error('Error fetching toys:', error);
      }
    };
    fetchToys();

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleTypeChange = (type) => {
    setFormData({ ...formData, type });
  };

  const handleDurationChange = (duration) => {
    setFormData({ ...formData, duration: duration === formData.duration ? '' : duration });
  };

  const handleIntensityChange = (intensity) => {
    setFormData(prevState => ({
      ...prevState,
      intensity: prevState.intensity.includes(intensity)
        ? prevState.intensity.filter(i => i !== intensity)
        : [...prevState.intensity, intensity]
    }));
  };

  const handleToyChange = (toyId) => {
    setFormData(prevState => ({
      ...prevState,
      toys: prevState.toys.includes(toyId)
        ? prevState.toys.filter(id => id !== toyId)
        : [...prevState.toys, toyId]
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const dataToSubmit = { ...formData };
      if (dataToSubmit.toys.length === 0) {
        dataToSubmit.toys = ['all'];
      }
      await axios.post('http://localhost:1812/api/truthordare', dataToSubmit);
      alert('Truth or Dare added successfully!');
      onClose();
    } catch (error) {
      console.error('Error adding Truth or Dare:', error);
      alert('Failed to add Truth or Dare. Please try again.');
    }
  };

  const filteredToys = availableToys.filter(toy =>
    toy.name.toLowerCase().includes(toySearchTerm.toLowerCase())
  );

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Add New Truth or Dare</SectionTitle>
        <Form onSubmit={handleSubmit}>
          <GroupContainer>
            <SubTitle>SÃ©lectionnez le type</SubTitle>
            <ChipsContainer>
              <Chip
                type="button"
                selected={formData.type === 'truth'}
                onClick={() => handleTypeChange('truth')}
              >
                Truth
              </Chip>
              <Chip
                type="button"
                selected={formData.type === 'dare'}
                onClick={() => handleTypeChange('dare')}
              >
                Dare
              </Chip>
            </ChipsContainer>
          </GroupContainer>



          <GroupContainer>
            <SubTitle>Action ou VÃ©ritÃ©</SubTitle>
            <Textarea
              name="template"
              value={formData.template}
              onChange={handleChange}
              placeholder="Enter the question or dare"
              required
            />
          </GroupContainer>

          {formData.type === 'dare' && (
            <>
              <GroupContainer>
                <SubTitle>Combien de temps ?</SubTitle>
                <ChipsContainer>
                  {durationOptions.map((option) => (
                    <Chip
                      key={option.value}
                      selected={formData.duration === option.value}
                      onClick={() => handleDurationChange(option.value)}
                    >
                      {option.label}
                    </Chip>
                  ))}
                </ChipsContainer>
              </GroupContainer>

              <GroupContainer>
                <SubTitle>Niveau d'intensitÃ© ?</SubTitle>
                <ChipsContainer>
                  {intensityOptions.map((intensity) => (
                    <Chip
                      key={intensity}
                      selected={formData.intensity.includes(intensity)}
                      onClick={() => handleIntensityChange(intensity)}
                    >
                      {intensity}
                    </Chip>
                  ))}
                </ChipsContainer>
              </GroupContainer>

              <GroupContainer>
                <SubTitle>Toys</SubTitle>
                <SelectWrapper ref={dropdownRef}>
                  <StyledSelect onClick={() => setIsDropdownOpen(!isDropdownOpen)}>
                    <Input
                      type="text"
                      placeholder="Search and select toys..."
                      value={toySearchTerm}
                      onChange={(e) => setToySearchTerm(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                    />
                  </StyledSelect>
                  {isDropdownOpen && (
                    <DropdownList>
                      {filteredToys.map(toy => (
                        <CheckboxLabel key={toy._id}>
                          <input
                            type="checkbox"
                            checked={formData.toys.includes(toy.name_id)}
                            onChange={() => handleToyChange(toy.name_id)}
                          />
                          {toy.name}
                        </CheckboxLabel>
                      ))}
                    </DropdownList>
                  )}
                </SelectWrapper>
              </GroupContainer>
            </>
          )}

          <ButtonContainer>
          <Button type="button" onClick={onClose}>Cancel</Button>
            <Button type="submit">Add</Button>

          </ButtonContainer>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddTruthOrDareModal;
