import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 40px;
  border-radius: 16px;
`;

const CardsContainer = styled.div`
  display: flex;
  gap: 20px;
`;

const Card = styled.div`
  width: 200px;
  height: 300px;
  background-color: #FFD700;
  border-radius: 16px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: rotate(7deg) scale(1.05);
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ActionVerite = () => {
  return (
    <Container>
      <CardsContainer>
        <Card image={require('../images/junebaby.png')} />
        <Card image={require('../images/junebaby.png')} />
      </CardsContainer>
      <ButtonContainer>
        <Button>
          <span role="img" aria-label="Tour de William">üë§</span> Au tour de William
        </Button>
        <Button>
          <span role="img" aria-label="I love it">‚ù§Ô∏è</span> I love it
        </Button>
      </ButtonContainer>
    </Container>
  );
};

export default ActionVerite;
