import React from 'react';
import Navbar from './components/navbar/navbar';
import TransitionPage from './components/TransitionPage';
import CardsWrapper from './components/CardsWrapper';  // Importez votre CardsWrapper
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext'; // Importez le PageProvider

const MainContent = () => {
  const { selectedCard, isLottieVisible, finishAnimation, resetCard } = useCard();

  return (
    <div>
      <Navbar />  {/* Navbar avec NavbarLeft et NavbarRight */}

      {/* Afficher le CardsWrapper tant qu'aucune carte n'est sélectionnée */}
      {!selectedCard && (
        <CardsWrapper setBackgroundColor={(color) => document.body.style.backgroundColor = color} />
      )}

      {/* Afficher TransitionPage après la sélection d'une carte */}
      {selectedCard && (
        <TransitionPage
          selectedCard={selectedCard}
          onComplete={finishAnimation}
          onBack={resetCard}
        />
      )}
    </div>
  );
};

const MainComponent = () => (
  <CardProvider>
    <PageProvider>
      <MainContent />
    </PageProvider>
  </CardProvider>
);

export default MainComponent;
