import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import NavbarRight from './navbar/navbarright';
import CardRenderer from './CardRenderer';
import gsap from 'gsap';
import Logo from './Logo';
import GrainEffect from './GrainEffect';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const LogoContainer = styled.div`
  position: absolute;
  top: 50%; /* Start from the center */
  left: 50%;
  transform: translate(-50%, -50%) scale(1); /* Centered and full size initially */
  transform-origin: center top;
  z-index: 100000;
`;

const CardsContainer = styled.div`
  opacity: ${({ opacity }) => opacity};
  transform: translateY(${({ isAnimated }) => (isAnimated ? '0' : '50px')});
  transition: ${({ isAnimated }) => (isAnimated ? 'transform 0.8s ease, opacity 0.8s ease' : 'none')};
  pointer-events: ${({ clickable }) => (clickable ? 'auto' : 'none')};
  z-index: 20;
`;

const GrainContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  background-color: white; /* Overlay de fond blanc */
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const MainContent = ({ backgroundColor, setBackgroundColor }) => {
  const { selectedCard, resetCards } = useCard();
  const [cardsOpacity, setCardsOpacity] = useState(0); // Commencer avec opacité 0
  const [isAnimated, setIsAnimated] = useState(false); // Commencer avec l'animation désactivée
  const [cardsClickable, setCardsClickable] = useState(false); // Les cartes ne sont pas cliquables au départ
  const cardsRef = useRef(null);
  const logoRef = useRef(null);
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);

  const handleEnter = () => {
    // Animer le logo pour le réduire et le déplacer en haut
    gsap.to(logoRef.current, {
      duration: 1.5,
      scale: 0.2,
      y: -window.innerHeight / 2 + 50, // Déplacer en haut de l'écran avec 50px de marge
      ease: 'power2.inOut',
      onComplete: () => {
        // Une fois l'animation du logo terminée, afficher les cartes
        setIsAnimated(true);
        gsap.to(cardsRef.current, {
          opacity: 1,
          y: 0,
          duration: 0.8,
          ease: 'power4.out',
          onComplete: () => {
            setCardsClickable(true); // Rendre les cartes cliquables
          },
        });
        gsap.to(overlayRef.current, {
          duration: 0.5,
          opacity: 0,
          pointerEvents: 'none',
          onComplete: () => {
            overlayRef.current.style.display = 'none'; // Cacher l'overlay après l'animation
          },
        });
      },
    });
  };

  useEffect(() => {
    // Réinitialiser le fond à blanc si aucune carte n'est sélectionnée (c'est-à-dire retour à l'état initial)
    if (!selectedCard) {
      setBackgroundColor('#FFFFFF');
    }
  }, [selectedCard, setBackgroundColor]);

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <GrainContainer>
        <GrainEffect />
      </GrainContainer>

      <LogoContainer ref={logoRef}>
        <Logo />
      </LogoContainer>

      <Navbar />
      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer
          ref={cardsRef}
          opacity={cardsOpacity}
          isAnimated={isAnimated}
          clickable={cardsClickable}
        >
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}

      <OverlayContainer ref={overlayRef}>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

export default MainContent;
