import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import Logo from './components/Logo';
import styled from 'styled-components';

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000002;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%; 
  pointer-events: all;
`;

const MainComponent = () => {
  const [loading, setLoading] = useState(true);
  const [animateLogo, setAnimateLogo] = useState(false);

  const handleEnter = () => {
    setAnimateLogo(true);  // Déclenche l'animation du logo

    // Retirer le LoadingScreen après un délai
    setTimeout(() => {
      setLoading(false);
    }, 1000); // Correspond à la durée de l'animation
  };

  return (
    <CardProvider>
      <PageProvider>
        <div>
          {/* Le logo est au-dessus du LoadingScreen */}
          <Logo animate={animateLogo} />
          {loading && (
            <OverlayContainer>
              <ButtonContainer>
                <button onClick={handleEnter}>Entrer</button>
              </ButtonContainer>
            </OverlayContainer>
          )}
          {!loading && (
            <div>
              <Navbar />
              <CardsWrapper />
              <GrainEffect />
            </div>
          )}
        </div>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
