import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const CardsBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #faf3d9;
  z-index: 100000000;
`;

const Card = styled.div`
  width: 300px;
  height: 200px;
  background-color: transparent;
  position: absolute;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.1);
  opacity: 0.8;

  &.card1 { background-color: #d7f07d; top: -80px; left: -100px; transform: rotate(15deg); }
  &.card2 { background-color: #fcba83; top: 50px; right: -120px; transform: rotate(-12deg); }
  &.card3 { background-color: #5ed5fb; bottom: -50px; left: -100px; transform: rotate(-25deg); }
  &.card4 { background-color: #f979b4; bottom: -80px; right: -110px; transform: rotate(20deg); }
  &.card5 { background-color: #fc83d7; top: 20%; left: 50%; transform: rotate(10deg); }
  &.card6 { background-color: #abffb0; bottom: 20%; right: 40%; transform: rotate(-10deg); }
`;

const Content = styled.div`
  position: relative;
  z-index: 10;
  text-align: center;
  max-width: 800px;
`;

const Title = styled.h1`
  font-size: 4rem;
  margin: 0;
  color: #191919;
  opacity: 0;
  animation: fadeIn 2s forwards;
`;

const Subtitle = styled.h2`
  font-size: 3rem;
  margin-top: 0.5rem;
  color: #191919;
  opacity: 0;
  animation: fadeIn 3s forwards;
`;

const fadeInKeyframes = `
  @keyframes fadeIn {
    to {
      opacity: 1;
    }
  }
`;

function App() {
  const titleRef = useRef(null);
  const subtitleRef = useRef(null);

  useEffect(() => {
    // Ajoutez une logique d'animation ici si nécessaire
  }, []);

  return (
    <>
      <style>{fadeInKeyframes}</style>
      <CardsBackground>
        <Card className="card1"></Card>
        <Card className="card2"></Card>
        <Card className="card3"></Card>
        <Card className="card4"></Card>
        <Card className="card5"></Card>
        <Card className="card6"></Card>
        <Content>
          <Title ref={titleRef}>A NIGHT OF CONVERSATION</Title>
          <Subtitle ref={subtitleRef}>EXPLORING THE CONFLICTS IN DESIGN</Subtitle>
        </Content>
      </CardsBackground>
    </>
  );
}

export default App;
