import React, { useRef, useState } from 'react';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import Logo from './Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';
import CardRenderer from './CardRenderer';
import NavbarRight from './navbar/navbarright';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const CardsContainer = styled.div`
  opacity: ${({ opacity }) => opacity}; /* L'opacité est contrôlée par l'état */
  transform: translateY(${({ isAnimated }) => (isAnimated ? '0' : '50px')});
  transition: ${({ isAnimated }) => (isAnimated ? 'transform 0.8s ease, opacity 0.8s ease' : 'none')};
`;

const MainComponentContent = () => {
  const { selectedCard, resetCards } = useCard();
  const [animateLogo, setAnimateLogo] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('#FFFFFF'); 
  const [cardsOpacity, setCardsOpacity] = useState(0); // État pour gérer l'opacité de CardsWrapper
  const [isAnimated, setIsAnimated] = useState(false); // Pour contrôler l'animation une seule fois
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }

        if (cardsRef.current) {
          gsap.to(cardsRef.current, {
            opacity: 1,
            y: 0,
            duration: 0.1, // Animation plus longue pour plus de fluidité
            ease: 'power4.out',
            delay: 0.1,
          });
        }

        setIsAnimated(true); // Active l'animation pour la première fois
        setCardsOpacity(1); // Passe l'opacité des cartes à 1 après l'animation
      },
    });
  };

  const handleXIconClick = () => {
    resetCards(); // Réinitialise la sélection des cartes
    setIsAnimated(false); // Désactive l'animation pour le retour
    setCardsOpacity(1); // Assure que les cartes sont visibles
  };

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <Logo animate={animateLogo} />
      <Navbar />

      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer ref={cardsRef} opacity={cardsOpacity} isAnimated={isAnimated}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}

      <WhiteOverlay ref={overlayRef} />

      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

export default MainComponentContent;
