import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 40px;
  border-radius: 16px;
  height: 100vh;
  width: 100vw;
  position: relative;

`;

const CardsContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
`;

const Card = styled.div`
  width: 392px;
  height: 548px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.6s cubic-bezier(0.22, 1, 0.36, 1);

  ${({ position }) => position === 'left' && `
    top: 50%;
    left: calc(50% - 196px); 
    transform: translate(-50%, -50%) rotate(-10deg);
  `}

  ${({ position }) => position === 'right' && `
    top: 50%;
    left: calc(50% + 196px); 
    transform: translate(-50%, -50%) rotate(10deg);
  `}

  ${({ isClicked, position }) => isClicked && `
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(0deg);
  `}

  ${({ isClicked, position }) => !isClicked && position === 'left' && `
    transform: translate(-200%, -50%) rotate(-10deg);
  `}

  ${({ isClicked, position }) => !isClicked && position === 'right' && `
    transform: translate(200%, -50%) rotate(10deg);
  `}
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ActionVerite = () => {
  const [clickedCard, setClickedCard] = useState(null);

  const handleCardClick = (card) => {
    setClickedCard(card);
  };

  return (
    <Container>
      <CardsContainer>
        <Card
          image={require('../images/junebaby.png')}
          position="left"
          isClicked={clickedCard === 'left'}
          onClick={() => handleCardClick('left')}
        />
        <Card
          image={require('../images/junebaby.png')}
          position="right"
          isClicked={clickedCard === 'right'}
          onClick={() => handleCardClick('right')}
        />
      </CardsContainer>
      <ButtonContainer>
        <Button>
          <span role="img" aria-label="Tour de William">üë§</span> Au tour de William
        </Button>
        <Button>
          <span role="img" aria-label="I love it">‚ù§Ô∏è</span> I love it
        </Button>
      </ButtonContainer>
    </Container>
  );
};

export default ActionVerite;
