import React, { useState, useRef } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import Logo from './components/Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center; /* Centrer verticalement */
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  z-index: 100000002;
  background-color: #fff;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
`;

const GrainOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  pointer-events: none;
  z-index: 100000004;
`;

const CardsContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  opacity: 0; /* Initialement invisible */
`;

const MainComponent = () => {
  const [animateLogo, setAnimateLogo] = useState(false);
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const navbarRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    // Utiliser GSAP pour animer la disparition du fond blanc et du bouton
    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        overlayRef.current.style.display = 'none';

        // Animation pour afficher la Navbar
        gsap.fromTo(navbarRef.current, { opacity: 0, y: -50 }, { opacity: 1, y: 0, duration: 1, ease: 'power2.out' });

        // Animation pour les cartes (chaque enfant de CardsWrapper)
        gsap.fromTo(cardsRef.current.children, { y: 200, opacity: 0 }, { y: 0, opacity: 1, duration: 1, ease: 'power2.out', stagger: 0.2 });
      }
    });
  };

  return (
    <CardProvider>
      <PageProvider>
        <BackgroundContainer>
          <Logo animate={animateLogo} />

          {/* Navbar cachée au début */}
          <div ref={navbarRef} style={{ opacity: 0 }}>
            <Navbar />
          </div>

          {/* Cartes cachées au début et centrées */}
          <CardsContainer ref={cardsRef}>
            <CardsWrapper />
          </CardsContainer>

          <GrainEffect /> 

          <WhiteOverlay ref={overlayRef} />

          <OverlayContainer>
            <ButtonContainer ref={buttonRef}>
              <button onClick={handleEnter}>Entrer</button>
            </ButtonContainer>
          </OverlayContainer>

        </BackgroundContainer>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
