import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.7);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  padding: 20px;
  border-radius: 10px;
  width: 80%;
  max-width: 500px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const Input = styled.input`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Select = styled.select`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #ff4500;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #ff5722;
  }
`;

const AddTruthOrDareModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    type: 'truth',
    player: 'firstName1',
    template: '',
    duration: '',
    intensity: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:1812/api/truthordare', formData);
      alert('Truth or Dare added successfully!');
      onClose();
    } catch (error) {
      console.error('Error adding Truth or Dare:', error);
      alert('Failed to add Truth or Dare. Please try again.');
    }
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <h2>Add New Truth or Dare</h2>
        <Form onSubmit={handleSubmit}>
          <Select name="type" value={formData.type} onChange={handleChange}>
            <option value="truth">Truth</option>
            <option value="dare">Dare</option>
          </Select>
          <Select name="player" value={formData.player} onChange={handleChange}>
            <option value="firstName1">Player 1</option>
            <option value="firstName2">Player 2</option>
          </Select>
          <Input
            name="template"
            value={formData.template}
            onChange={handleChange}
            placeholder="Enter the question or dare"
            required
          />
          <Input
            name="duration"
            type="number"
            value={formData.duration}
            onChange={handleChange}
            placeholder="Duration (in seconds, optional)"
          />
          <Input
            name="intensity"
            value={formData.intensity}
            onChange={handleChange}
            placeholder="Intensity (optional)"
          />
          <Button type="submit">Add</Button>
          <Button type="button" onClick={onClose}>Cancel</Button>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddTruthOrDareModal;
