import React, { useState } from 'react';
import Lottie from 'lottie-react';
import animationData from './test.json'; // Assurez-vous que le chemin vers le fichier Lottie est correct

function App() {
  const [isLottieVisible, setLottieVisible] = useState(false);

  const handleShowLottie = () => {
    console.log('Button clicked'); // Log pour vérifier que le bouton est bien cliqué
    setLottieVisible(true);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <button onClick={handleShowLottie}>Show Lottie Animation</button>
      {isLottieVisible && (
        <div style={{ marginTop: '20px' }}>
          <Lottie animationData={animationData} loop={true} style={{ width: 300, height: 300 }} />
        </div>
      )}
    </div>
  );
}

export default App;
